import React from 'react';
import ReactDOM from 'react-dom';
import Note from './components/course.js'


const Header = ({ data }) => {
  return <h2>{data.name}</h2>
}

const Line = ({ data }) => {
  return (<li>{data.name} {data.exercises}</li>)
}

const Total = ({ data }) => {
  const sum = data.parts.reduce(
    (prevValue, currentValue) => prevValue + currentValue.exercises, 0
  )

  return (
    <p><b>Total of {sum} Exercises</b></p>
  )
}

const Course = ({ data }) => {

  console.log(data)
  return (
    <div>
      <Header data={data} />
      <ul>
        {data.parts.map(line =>
          <Line key={line.id} data={line} />
        )}
      </ul>
      <Total data={data} />
    </div>
  )
}

const App = ({course}) => {

  return (
    <div>
      <h1>Web development curriculum</h1>
      {course.map(line => <Course data={line} />)}
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
